mapper.select:
        select DISTINCT o.*
        from t_Production_Procedure_Confirm p LEFT JOIN t_Order o ON p.orderNo = o.orderNo
         where ((p.type = 2 and p.isChange=0) or (p.type =1 and p.isChange=0))
         and p.isDelete = 0
        and DATE_FORMAT(p.completeTime, '%Y-%m' ) = #{date}
        and p.workshop = #{deptName,jdbcType=VARCHAR}
        ORDER BY o.createTime DESC
;

select a.productNo,a.unit, max(a.num) num from
(
  select DISTINCT p.productNo, sum(p.num) AS num, pp.unit
  from t_Production_Procedure_Confirm p
  LEFT JOIN t_Product pp ON p.productNo = pp.productNo
  where ((p.type = 2 and p.isChange=0) or (p.type =1 and p.isChange=0))
   and p.isDelete = 0
  and p.orderNo = #{orderNo,jdbcType=VARCHAR}
  and p.workshop = #{deptName,jdbcType=VARCHAR}
  and DATE_FORMAT(p.completeTime, '%Y-%m' ) = #{date}
  group by p.productNo,p.procedureId, pp.unit
  ) a group by a.productNo,a.unit

SELECT DISTINCT
pp.*
from t_Production_Procedure_Confirm p LEFT JOIN t_Product_Procedure pp ON pp.id = p.procedureId
where  p.productNo = #{productNo,jdbcType=VARCHAR}
and p.orderNo = #{orderNo,jdbcType=VARCHAR}
and p.isChange=0
 and p.isDelete = 0
 and p.workshop = #{deptName,jdbcType=VARCHAR}
 and DATE_FORMAT(p.completeTime, '%Y-%m' ) = #{date}

 SELECT DISTINCT
p.empId,
sum(p.num) num,
e.name AS empName
from t_Production_Procedure_Confirm p  LEFT JOIN t_Emp e ON e.id = p.empId
where  p.productNo = #{productNo,jdbcType=VARCHAR}
and p.orderNo = #{orderNo,jdbcType=VARCHAR}
and p.procedureId = #{id,jdbcType=INTEGER}
and DATE_FORMAT(p.completeTime, '%Y-%m' ) = #{date}
and p.isChange=0
 and p.isDelete = 0
 and p.workshop = #{deptName,jdbcType=VARCHAR}
 group by p.empId,e.name

SELECT

FROM t_Production_Procedure_Confirm
WHERE 1=1

    AND orderNo = #{orderNo,jdbcType=VARCHAR}


    AND productNo = #{productNo,jdbcType=VARCHAR}


    AND procedureId = #{procedureId,jdbcType=INTEGER}


    AND procedureName = #{procedureName,jdbcType=VARCHAR}


    AND empId = #{empId,jdbcType=INTEGER}


    AND  type = #{type,jdbcType=INTEGER}


    AND operator = #{operator,jdbcType=VARCHAR}


    AND productionNo = #{productionNo,jdbcType=VARCHAR}


    AND luoId = #{luoId,jdbcType=INTEGER}


    AND pmOutBillNo = #{pmOutBillNo,jdbcType=VARCHAR}


    AND isChange = #{isChange,jdbcType=INTEGER}

AND isDelete = 0

SELECT

from t_Production_Procedure_Confirm p
where  p.productNo = #{productNo,jdbcType=VARCHAR}
and p.orderNo = #{orderNo,jdbcType=VARCHAR}
and p.procedureId = #{id,jdbcType=INTEGER}
 and p.isDelete = 0
 and p.workshop = #{deptName,jdbcType=VARCHAR}
and DATE_FORMAT(p.completeTime, '%Y-%m' ) = #{date}
order by p.completeTime desc

    (
        SELECT
            p.procedureName NAME,
            p.num,
            p.price,
            p.money
        FROM
            t_Production_Procedure_Confirm p
        WHERE
            DATE_FORMAT(p.completeTime, "%Y-%m-%d") = #{date}
        AND p.empId = #{empId}
        AND p.orderNo = #{orderNo}
        AND p.productNo = #{productNo}

            AND p.pmOutBillNo = #{billNo}


            AND p.productionNo = #{billNo}


            AND p.productionNo = #{billNo} AND p.luoId = #{luoId}

        AND (p.isChange=0 or p.isChange=2)
        AND p.isDelete = 0
    )
    UNION ALL
	(
		SELECT
			s.procedureName NAME,
			s.num,
			s.price,
			s.money
		FROM
			t_Production_Procedure_Scan s
		WHERE
			DATE_FORMAT(s.createTime, "%Y-%m-%d") = #{date}
		AND s.empId = #{empId}
        AND s.orderNo = #{orderNo}
        AND s.productNo = #{productNo}

            AND s.pmOutBillNo = #{billNo}


            AND s.productionNo = #{billNo}


            AND s.productionNo = #{billNo} AND s.luoId = #{luoId}

		AND s. STATUS = 0
		AND s.isDelete = 0
	)

SELECT a.* FROM ( ( select DATE_FORMAT(p.completeTime, "%Y-%m") yearMonth, e.name name, p.workshop dept, w.name work, p.orderNo orderNo, p.productNo productNo, CASE WHEN p.luoId IS NOT NULL THEN CONCAT(p.productionNo, '-', l.sort) WHEN p.luoId IS NULL AND p.productionNo IS NOT NULL THEN p.productionNo WHEN p.pmOutBillNo IS NOT NULL THEN p.pmOutBillNo END AS productionNo, p.procedureName procedureName, p.num num, p.price price, p.money money, p.createTime date, p.inspectionor checker, p.createTime sortTime from t_Production_Procedure_Confirm p LEFT JOIN t_Emp e ON p.empId = e.id LEFT JOIN t_Emp_work w ON w.id = e.workId LEFT JOIN t_Production_Luo l ON p.luoId = l.id where (p.isChange=0 or p.isChange=2) AND p.isDelete = 0
AND e.name = #{name}
AND e.deptId = #{deptId}
AND e.workId = #{workId}
AND p.orderNo = #{orderNo}
AND p.productNo = #{productNo}
AND p.procedureName = #{procedureName}
AND DATE_FORMAT(p.completeTime, "%Y-%m") = #{yearMonth}
AND p.createTime >= #{startDate}
AND p.createTime <= #{endDate}
) UNION ALL ( SELECT DATE_FORMAT(s.createTime, "%Y-%m") yearMonth, e.name name, d.deptName dept, w.name work, s.orderNo orderNo, s.productNo productNo, CASE WHEN s.luoId IS NOT NULL THEN CONCAT(s.productionNo, '-', l.sort) WHEN s.luoId IS NULL AND s.productionNo IS NOT NULL THEN s.productionNo WHEN s.pmOutBillNo IS NOT NULL THEN s.pmOutBillNo END AS productionNo, s.procedureName procedureName, s.num num, s.price price, s.money money, null as date, null as checker, s.createTime sortTime FROM t_Production_Procedure_Scan s LEFT JOIN t_Emp e ON s.empId = e.id LEFT JOIN t_Emp_work w ON w.id = e.workId LEFT JOIN t_Production_Luo l ON s.luoId = l.id LEFT JOIN t_SysDept d ON d.id = e.deptId WHERE s. STATUS = 0 AND s.isDelete = 0
AND e.name = #{name}
AND e.deptId = #{deptId}
AND e.workId = #{workId}
AND s.orderNo = #{orderNo}
AND s.productNo = #{productNo}
AND s.procedureName = #{procedureName}
AND DATE_FORMAT(s.createTime, "%Y-%m") = #{yearMonth}
AND 1 !=1
AND 1 !=1
) ) a ORDER BY a.sortTime DESC

mapper.update:
update t_Production_Procedure_Confirm
SET isDelete = 1 , lastModifyTime = now()
WHERE orderNo = #{orderNo,jdbcType=VARCHAR} and productNo =#{productNo}

    and productionNo = #{productionNo}


    and luoId = #{louId}


    and pmOutBillNo = #{billOutNo}


  and procedureId = #{procedureId}

and type in(1,3) and isChange = 0

update t_Production_Procedure_Confirm p
set  p.isDelete = 1
where  p.productNo = #{productNo,jdbcType=VARCHAR}
and p.orderNo = #{orderNo,jdbcType=VARCHAR}
and p.procedureId = #{id,jdbcType=INTEGER}
and p.type = 2
and p.isChange=0
 and p.isDelete = 0
 and p.workshop = #{deptName,jdbcType=VARCHAR}
 and DATE_FORMAT(p.completeTime, '%Y-%m' ) = #{date}

update t_Production_Procedure_Confirm p
set  p.isChange = 1
where  p.productNo = #{productNo,jdbcType=VARCHAR}
and p.orderNo = #{orderNo,jdbcType=VARCHAR}
and p.procedureId = #{id,jdbcType=INTEGER}
and (p.type =1 or p.type=3)
and p.isDelete = 0
and p.workshop = #{deptName,jdbcType=VARCHAR}
and DATE_FORMAT(p.completeTime, '%Y-%m' ) = #{date}

update t_Production_Procedure_Confirm
set isChange = 2
where id in
(

    #{no}

)
